document.addEventListener("DOMContentLoaded",(()=>{const e=document.getElementById("musicCheckbox"),t=document.getElementById("videoCheckbox");document.getElementById("submitButton");function c(){const e=document.querySelector("#musicCheckbox").checked,t=document.querySelector("#videoCheckbox").checked,c=document.querySelector("button[type=submit]");c.disabled=!e&&!t}e.addEventListener("change",(()=>{e.checked&&(t.checked=!1)})),t.addEventListener("change",(()=>{t.checked&&(e.checked=!1)})),c();const n=document.querySelectorAll("input[type=checkbox]");for(const e of n)e.addEventListener("change",c)}));